    /**
     * Add table data in PDF
     *
     * @param headerList
     * @param data
     * @param document
     * @throws Exception
     */
    private void addTableData(List<String> headerList, Map<String, List<List<String>>> data, Document document, String serviceName)
            throws Exception {
        //LOGGER.log(Level.INFO, "PDF::serviceName:: {0}", serviceName);
        int noOfColumns = 0;

        for (String key : data.keySet()) {
            //LOGGER.log(Level.INFO, "PDF::Key:: {0}", key);
            List<List<String>> dataList = data.get(key);
            for (List<String> mapp : dataList) {
                if (noOfColumns < mapp.size()) {
                    //LOGGER.log(Level.INFO, "PDF::Map size:: {0} :: NumberOfColumns:: {1}", new Object[]{mapp.size(), noOfColumns});
                    noOfColumns = mapp.size();
                }
            }
        }

        if (noOfColumns == 0) {
            throw new Exception("No data to add in report!");
        }
        //noOfColumns = noOfColumns + 1; // One for Key
        PdfPTable table = new PdfPTable(noOfColumns); // 3 columns.
        //table.setTableEvent(this.new DottedCells());
        table.setWidthPercentage(100); // Width 100%
        table.setSpacingBefore(0f); // Space before table
        table.setSpacingAfter(0f); // Space after table

        // Set Column widths
        float[] columnWidths = new float[noOfColumns];

        for (int i = 0; i < noOfColumns; i++) {
            if (serviceName.contains("Prescription")) {
                switch (i) {
                    case 4:
                    case 5:
                    case 6:
                        columnWidths[i] = 0.5f;
                        break;
                    case 0:
                        columnWidths[i] = 0.5f;
                        break;
                    case 9:
                        columnWidths[i] = 1.5f;
                        break;
                    case 8:
                        columnWidths[i] = 0.8f;
                        break;
                    case 10:
                    case 3:
                        columnWidths[i] = 1.5f;
                        break;
                    default:
                        columnWidths[i] = 1.f;
                        break;
                }
            } else if (serviceName.contains("Patient")) {
                switch (i) {
                    case 0:
                        columnWidths[i] = 0.5f;
                        break;
                    case 1:
                        columnWidths[i] = 1.5f;
                        break;
                    case 2:
                        columnWidths[i] = 1.0f;
                        break;
                    case 3:
                        columnWidths[i] = 2.0f;
                        break;
                    default:
                        columnWidths[i] = 1.f;
                        break;
                }
            } else {
                columnWidths[i] = 1.f;
            }
        }

        table.setWidths(columnWidths);

        for (int i = 0; i < noOfColumns; i++) {
            String lbl = "";
            if (i >= headerList.size()) {
                lbl = "NOT-DEFINED";
            } else {
                lbl = headerList.get(i);
            }
            PdfPCell cell1 = new PdfPCell(new Paragraph(lbl, blackBold));
            //cell1.setCellEvent(this.new DottedCell());
            cell1.setBorderColorTop(BaseColor.WHITE);
            cell1.setBorderColorLeft(BaseColor.WHITE);
            cell1.setBorderColorRight(BaseColor.WHITE);
            cell1.setBorderColorBottom(BaseColor.BLACK);
            //cell1.setPaddingLeft(10);
            cell1.setBorderWidthBottom(0.5f);
            if (serviceName.contains("Prescription")) {
                if (i == 4 || i == 5 || i == 6 || i == 8) {
                    cell1.setHorizontalAlignment(Element.ALIGN_RIGHT);
                    cell1.setVerticalAlignment(Element.ALIGN_LEFT);
                } else {
                    cell1.setHorizontalAlignment(Element.ALIGN_LEFT);
                    cell1.setVerticalAlignment(Element.ALIGN_LEFT);
                }
            } else {
                cell1.setHorizontalAlignment(Element.ALIGN_LEFT);
                cell1.setVerticalAlignment(Element.ALIGN_LEFT);
            }
            //cell1.setFollowingIndent(10);
            table.addCell(cell1);
        }
        for (String key : data.keySet()) {
            List<List<String>> dataList = data.get(key);

            for (List<String> mapp : dataList) {
                int i = 0;
                for (String value : mapp) {

                    if (value == null || value.trim().equals("")) {
                        value = "N/A";
                    }

                    Paragraph p1 = null;
                    if (value != null && value.contains("<b>")) {
                        value = value.replaceAll("<b>", "*");
                        value = value.replaceAll("</b>", "*");
                        char[] carr = value.toCharArray();
                        StringBuilder builder = new StringBuilder();
                        boolean isBoldStarted = false;
                        p1 = new Paragraph();
                        for (char c : carr) {
                            if (c == '*') {
                                if (isBoldStarted) {
                                    Chunk text21 = new Chunk(builder.toString(), blackBold);
                                    //builder.append(c);

                                    isBoldStarted = false;
                                    builder = new StringBuilder();
                                    p1.add(text21);
                                } else {
                                    Chunk text21 = new Chunk(builder.toString(), blackNormal);
                                    isBoldStarted = true;
                                    p1.add(text21);
                                    builder = new StringBuilder();
                                }
                            } else {
                                builder.append(c);
                            }
                        }

                        if (builder.toString().length() > 0) {
                            Chunk text21 = new Chunk(builder.toString(), blackNormal);
                            isBoldStarted = false;
                            builder = new StringBuilder();
                            p1.add(text21);
                        }
                    } else {
                        p1 = new Paragraph(value, blackNormal_table);
                    }

                    PdfPCell cell2 = new PdfPCell(p1);

                    //PdfPCell cell2 = new PdfPCell(new Paragraph(value, blackNormal_table));
                    //cell2.setCellEvent(this.new DottedCell());
                    //cell2.setBorderColor(BaseColor.GRAY);
                    cell2.setBorderWidth(0.0f);
                    //cell2.setPaddingLeft(10);

                    if (serviceName.contains("Prescription")) {
                        if (i == 4 || i == 5 || i == 6 || i == 8) {
                            cell2.setHorizontalAlignment(Element.ALIGN_RIGHT);
                            cell2.setVerticalAlignment(Element.ALIGN_LEFT);
                        } else {
                            cell2.setHorizontalAlignment(Element.ALIGN_LEFT);
                            cell2.setVerticalAlignment(Element.ALIGN_LEFT);
                        }
                    } else {
                        cell2.setHorizontalAlignment(Element.ALIGN_LEFT);
                        cell2.setVerticalAlignment(Element.ALIGN_LEFT);
                    }

                    //cell2.setFollowingIndent(10);
                    table.addCell(cell2);
                    i++;
                }
            }
        }

        document.add(table);
    }

